export class PerformanceModel {
	constructor(data) {
		this.id = data.data.userId;
		this.kind = data.kind;
		this.kindValues = data.data;
	}

	getTranslatedKind() {
		let values = this.kind;
		let kindArray = Object.keys(this.kind).map(function (key) {
			return {
				Number: key,
				values: values.value,
			};
		});
		console.log(kindArray);
		return kindArray;
	}

	getProcessedDatas() {
		console.log(this.getTranslatedKind());
		let kindValues = this.kind;

		let datas = [];
		let values = [];
		kindValues.forEach((value) => {
			values.push(value.value);
		});

		kindValues.forEach((kindValue) => {
			datas.push({
				subject: Object.values(this.kind)[kindValue.kind - 1],
				A: kindValue.value,
				fullMark: Math.max(...values),
			});
		});
		return datas;
	}
}
