import KeyDatas from '../../components/KeyDatas/KeyDatas';

const Dashboard = () => {
	const { id } = useParams();
	const [userId, setUserId] = useContext(UserContext);

	useEffect(() => {
		setUserId(id);
	}, [id, setUserId]);

	const user = new Datas('http://localhost:8080/user/', id);
	console.log(user);

