export class API {
	constructor(id, path) {
		this.mockedStatus = localStorage.getItem('mockedStatus');
		this.apiUrl =
			path === undefined ? `http://localhost:8080/user/${id}` : `http://localhost:8080/user/${id}/${path}`;
		this.mockedUrl = path === undefined ? `../../data/${id}/datas.json` : `../../data/${id}/${path}/datas.json`;
		this.url = this.mockedStatus === 'true' ? this.mockedUrl : this.apiUrl;
	}

	getDatas() {
		const fetchData = () => {
			return fetch(this.url)
				.then((response) => response.json())
				.then((data) => {
					// console.log(data.data);
					this.loading = false;
					this.error = false;
					this.errorMessage = '';
					this.datas = {
						datas: data.data,
						loading: false,
						error: false,
						errorMessage: '',
					};
					return this.datas;
				})
				.catch((error) => {
					this.loading = false;
					this.error = true;
					this.errorMessage = error;
					this.datas.push(this.datas, this.loading, this.error, this.errorMessage);
				});
		};
		this.datas = fetchData();
		return this.datas;
	}
}
