import { Api } from './api.js';

export class Datas {
	constructor(mockedState, url, id) {
		this.mockedState = mockedState;
		this.option = '';
		this.fetchAllUserData = new Api(url, id).getUserDatas(this.option);
		this.userDatas = [];
	}

	getUserInfos(url, id) {
		return this.fetchUserInfos(url, id);
	}

	getUserDatas() {
		this.fetchAllUserData().then((data) => {
			this.userDatas.push(data);
			this.option = '/performance';
			this.fetchAllUserData(this.option).then((data) => {
				this.userDatas.push(data);
				this.option = '/average-sessions';
				this.fetchAllUserData(this.option).then((data) => {
					this.userDatas.push(data);
					this.option = '//activity';
					this.fetchAllUserData(this.option).then((data) => {
						this.userDatas.push(data);
					});
				});
			});
		});
		return this.userDatas;
	}
}
