export class PerformanceModel {
	constructor(data) {
		this.id = data.data.userId;
		this.kind = data.kind;
		this.kindValues = data.data;
	}

	getTranslatedKind() {
		let kindValues = this.kindValues;
		kindValues.forEach((kindValue) => {
			switch (kindValue.kind) {
				case 0:
					Object.values(kindValue).forEach((value) => {
						if (value === 'cardio') {
							value = 'Cardio';
						}
					});
					break;
				case 1:
					Object.values(kindValue).forEach((value) => {
						if (value === 'energy') {
							value = 'Energie';
						}
					});
					break;
				case 2:
					Object.values(kindValue).forEach((value) => {
						if (value === 'endurance') {
							value = 'Endurance';
						}
					});
					break;
				case 3:
					Object.values(kindValue).forEach((value) => {
						if (value === 'strength') {
							value = 'Force';
						}
					});
					break;
				case 4:
					Object.values(kindValue).forEach((value) => {
						if (value === 'speed') {
							value = 'Vitesse';
						}
					});
					break;
				case 5:
					Object.values(kindValue).forEach((value) => {
						if (value === 'intensity') {
							value = 'Intensité';
						}
					});
					break;
				default:
					break;
			}
		});

		console.log(kindValues);
		return kindValues;
	}

	getProcessedDatas() {
		let kindValues = this.getTranslatedKind();

		let datas = [];
		let values = [];
		kindValues.forEach((value) => {
			values.push(value.value);
		});

		kindValues.forEach((kindValue) => {
			datas.push({
				subject: Object.values(this.kind)[kindValue.kind - 1],
				A: kindValue.value,
				fullMark: Math.max(...values),
			});
		});
		return datas;
	}
}
