export class API {
	constructor(id, path) {
		this.mockedStatus = localStorage.getItem('mockedStatus');
		this.apiUrl =
			path === undefined ? `http://localhost:8080/user/${id}` : `http://localhost:8080/user/${id}/${path}`;
		this.mockedUrl = path === undefined ? `../../data/${id}/datas.json` : `../../data/${id}/${path}/datas.json`;
		this.url = this.mockedStatus === 'true' ? this.mockedUrl : this.apiUrl;
	}

	useFetch() {
		this.datas = [];

		if (!this.url) return;

		const fetchData = async () => {
			this.loading = true;
			try {
				const response = await fetch(this.url);
				const json = await response.json();
				let datas = json.data;
				this.loading = false;
				this.error = false;
				this.errorMessage = '';
				this.datas.push(datas);
			} catch (error) {
				this.error = true;
				this.errorMessage = error;
			} finally {
				this.loading = false;
			}
			return {
				datas: await this.datas,
				loading: await this.loading,
				error: await this.error,
				errorMessage: await this.errorMessage,
			};
		};
		return fetchData();
		// fetchData();
		// return [this.datas, this.loading, this.error, this.errorMessage];
	}
}
