import { Api } from './api.js';

export class Datas {
	constructor(url, id) {
		this.url = url;
		this.id = id;
		this.fetchAllUserData = new Api(this.url, this.id);
	}

	getUserDatas() {
		const fetchUserData = async () => {
			try {
				const response = await fetch(this.url + this.id, {
					method: 'GET',
				});
				const data = await response.json();
				this.datas = data;
				console.log(data);
			} catch (error) {
				console.log(error);
			} finally {
				return this.datas;
			}
		};
		return 'fetchUserData() = ' + fetchUserData();
	}
}
