import { Api } from './api.js';

export class Datas {
	constructor(mockedState, url, id) {
		this.mockedState = mockedState;
		this.option = '';
		this.fetchAllUserData = new Api(url, id);
		this.userDatas = [];
	}

	getUserInfos(url, id) {
		return this.fetchUserInfos(url, id);
	}

	getUserDatas() {
		return this.fetchAllUserData.getUserDatas(this.option);
	}
}
