export class PerformanceModel {
	constructor(data) {
		this.id = data.data.userId;
		this.kind = data.kind;
		this.kindValues = data.data;
	}

	// getTranslatedKind() {
	// 	let values = this.kind;
	// 	let kindArray = Object.keys(this.kind).map(function (key) {
	// 		return {
	// 			Number: key,
	// 			values: values[key],
	// 		};
	// 	});
	// 	console.log(kindArray);
	// 	return kindArray;
	// }

	getTranslateKindString(x) {
		switch (x) {
			case 'cardio':
				return 'Cardio';
			case 'energy':
				return 'Energie';
			case 'endurance':
				return 'Endurance';
			case 'strength':
				return 'Force';
			case 'speed':
				return 'Vitesse';
			case 'intensity':
				return 'Intensité';
			default:
				return 'Aucun';
		}
	}

	getProcessedDatas() {
		let kindValues = this.kindValues;

		let datas = [];
		let values = [];
		kindValues.forEach((value) => {
			values.push(value.value);
		});

		kindValues.forEach((kindValue) => {
			datas.push({
				subject: Object.values(this.kind)[kindValue.kind - 1],
				A: this.getTranslateKindString(kindValue.value),
				fullMark: Math.max(...values),
			});
		});
		return datas;
	}
}
