import { ActivityModel } from '../models/ActivityModel';

export class API {
	constructor(id, path) {
		this.path = path;
		this.mockedStatus = localStorage.getItem('mockedStatus');
		this.apiUrl =
			path === undefined ? `http://localhost:8080/user/${id}` : `http://localhost:8080/user/${id}/${path}`;
		this.mockedUrl = path === undefined ? `../../data/${id}/datas.json` : `../../data/${id}/${path}/datas.json`;
		this.url = this.mockedStatus === 'true' ? this.mockedUrl : this.apiUrl;
		this.datas = [];
		this.loading = false;
		this.error = false;
		this.errorMessage = '';
	}

	async getUserDatas() {
		const fetchData = async () => {
			try {
				const response = await fetch(this.url);
				const data = await response.json();
				// console.log(data.data);
				this.loading = false;
				this.error = false;
				this.errorMessage = '';
				this.datas.push(data.data, this.loading, this.error, this.errorMessage);
				return this.datas;
			} catch (error) {
				this.loading = false;
				this.error = true;
				this.errorMessage = error;
				this.datas.push(this.datas, this.loading, this.error, this.errorMessage);
			}
		};
		fetchData();
		return this.datas;
	}

	async getActivityDatas() {
		const data = await this.getUserDatas();
		const activityDatas = new ActivityModel(data[0]);
		const processedDatas = activityDatas.getSessions();
		return processedDatas;
	}

	async getDatas() {
		if (!this.path) {
			return this.getUserDatas();
		} else if (this.path === 'activity') {
			return this.getActivityDatas();
		} else if (this.path === 'average-session') {
			return this.getAverageSessionDatas();
		} else if (this.path === 'performance') {
			return this.getPerformanceDatas();
		}
	}
}
