import React, { useContext } from 'react';
import { Radar, RadarChart, PolarGrid, PolarAngleAxis, PolarRadiusAxis, ResponsiveContainer } from 'recharts';

import useFetch from '../../../utils/hooks/hook';
import { UserContext } from '../../../utils/context/userContext';

import { PerformanceModel } from '../../../models/PerformanceModel';

import Loader from '../../Loader/Loader';

import './PerformanceGraph.css';

const PerformanceGraph = () => {
	const [userId] = useContext(UserContext);
	const { data, loading } = useFetch(userId, 'performance');

	if (loading === true) {
		return <Loader />;
	} else {
		const averageSessions = new PerformanceModel(data);
		const processedData = averageSessions.getProcessedDatas();
		console.log(processedData);

		return (
			<div className="performanceGraph" style={{ width: '50%', height: '50%' }}>
				<ResponsiveContainer>
					<RadarChart cx={80} cy={80} startAngle={90} outerRadius={80} data={processedData}>
						<PolarGrid />
						<PolarAngleAxis dataKey="subject" />
						<PolarRadiusAxis angle={90} domain={[0, 150]} />
						<Radar name="Mike" dataKey="A" stroke="#8884d8" fill="#8884d8" fillOpacity={0.6} />
					</RadarChart>
				</ResponsiveContainer>
			</div>
		);
	}
};

export default PerformanceGraph;
