	// 	activity,
	// 	'\nperformance datas : ',
	// 	performance,
	// 	'\naverage session datas : ',
	// 	averageSession
	// );

	/* Get the user data from the context */
	const [userId, setUserId] = useContext(UserContext);

	/* Set the user id in the context */
	useEffect(() => {
		setUserId(id);
	}, [id, setUserId]);

	//
