import { Link } from 'react-router-dom';
import {
	useState,
	// useEffect,
	// useContext
} from 'react';

// import { ApiContext } from '../../utils/context/apiContext';

import SportSeeLogo from '../../assets/icons/sportsee_logo.svg';

import './Login.css';

const Login = () => {
	const [toggle, setToggle] = useState(false);
	const toggleValue = !localStorage.getItem('mockedDatas') ? false : localStorage.getItem('mockedDatas');

	let datasOrigin = () => {
		!document.getElementById('switch').checked ? setToggle(false) : setToggle(true);
		localStorage.getItem('mockedDatas') === 'true'
			? localStorage.setItem('mockedDatas', false)
			: localStorage.setItem('mockedDatas', true);
		return toggle;
	};

	// const [mockedStatus, setMockedStatus] = useContext(ApiContext);

	// useEffect(() => {
	// 	setMockedStatus(toggle);
	// }, [setMockedStatus, toggle]);

	return (
		<div className="login">
			<img src={SportSeeLogo} alt="logo_SportSee" className="logo__SportSee" />
			<div className="users">
				<Link
					key={
						'user18'
						// + mockedStatus
					}
					to={`/user/18`}
				>
					<img
						src="https://doodleipsum.com/700/avatar?i=3cdf1e1375692dbe37689851492c2957"
						className="user__avatar"
						alt="Avatar by Gustavo Pedrosa"
					/>
					<h2>Cecilia</h2>
				</Link>
				<Link
					key={
						'user12'
						// + mockedStatus
					}
					to="/user/12"
				>
					<img
						src="https://doodleipsum.com/700/avatar?i=aaae4ac68a7288537c3192c776e5afab"
						className="user__avatar"
						alt="Avatar by Gustavo Pedrosa"
					/>
					<h2>Karl</h2>
				</Link>
			</div>
			<div className="datas__origin">
				<input
					type="checkbox"
					id="switch"
					className="checkbox"
					checked={localStorage.getItem('mockedDatas') === 'true' ? true : false}
					onClick={() => datasOrigin()}
				/>
				<label htmlFor="switch" className="toggle"></label>
				<h3> API / Mocked Datas</h3>
			</div>
		</div>
	);
};

export default Login;
