import React, { useContext, useEffect } from 'react';
import { useParams } from 'react-router';
import useFetch from '../../utils/hooks/hook';
import { UserContext } from '../../utils/context/context';

import Loader from '../../components/Loader/Loader';

import './Dashboard.css';

const Dashboard = () => {
	const { id } = useParams();
	const { data, isError, loading, errorMessage } = useFetch(`http://localhost:8080/user/`, id);
	const user = data;
	const error = errorMessage;
	const [userId, setUserId] = useContext(UserContext);

	useEffect(() => {
		setUserId(id);
	}, [id, setUserId]);

	return (
		<div key={userId} className="content">
			{isError ? (
				<div className="error">
					Une erreur est survenue lors du chargement des donn√©es : <br /> {error && error}
				</div>
			) : loading ? (
				<Loader />
			) : (
				<div className="dashboard__header">
					<h1>Bonjour {user.userInfos.firstName}</h1>
					<h2>F√©licitations! Vous avez explos√© vos objectifs hier üëèüèº</h2>
				</div>
			)}
		</div>
	);
};

export default Dashboard;
