import React, { useContext } from 'react';
import { RadialBarChart, RadialBar, Legend, ResponsiveContainer } from 'recharts';

import useFetch from '../../../utils/hooks/hook';
import { UserContext } from '../../../utils/context/userContext';

import { TodayScoreModel } from '../../../models/TodayScoreModel';

import Loader from '../../Loader/Loader';

import './ScoreGraph.css';

const ScoreGraph = () => {
	const [userId] = useContext(UserContext);
	const { data, loading } = useFetch(userId);

	const customizedLegend = () => {
		return (
			<div className="scoreGraph__legend">
				<div className="scoreGraph__legend__item">
					<div className="scoreGraph__legend__item__color" style={{ backgroundColor: '#FF0000' }}></div>
					<div className="scoreGraph__legend__item__text">Mauvais</div>
				</div>
				<div className="scoreGraph__legend__item">
					<div className="scoreGraph__legend__item__color" style={{ backgroundColor: '#FFA500' }}></div>
					<div className="scoreGraph__legend__item__text">Moyen</div>
				</div>
				<div className="scoreGraph__legend__item">
					<div className="scoreGraph__legend__item__color" style={{ backgroundColor: '#00FF00' }}></div>
					<div className="scoreGraph__legend__item__text">Bon</div>
				</div>
			</div>
		);
	};

	if (loading === true) {
		return <Loader />;
	} else {
		const todayScore = new TodayScoreModel(data);
		const processedData = todayScore.getTodayScore();
		console.log(processedData);
		return (
			<ResponsiveContainer width={'33%'} minHeight={263}>
				<RadialBarChart
					cx="50%"
					cy="50%"
					innerRadius="80%"
					outerRadius="80%"
					barSize={10}
					startAngle={90}
					endAngle={450}
					data={processedData}
				>
					<RadialBar minAngle={1} background={false} clockWise dataKey="score" />
					<Legend
						payload={[
							{ value: `${data.todayScore * 100}%`, color: '#282D30' },
							{ value: 'de votre objectif', color: '#74798C' },
						]}
						iconSize={0}
						layout="vertical"
						verticalAlign="middle"
						align="center"
						class="legend__style"
					/>
				</RadialBarChart>
			</ResponsiveContainer>
		);
	}
};

export default ScoreGraph;
