export class Api {
	constructor(url, id) {
		this.url = url;
		this.id = id;
		this.datas = {};
	}

	getUserDatas(option) {
		if (!this.url) {
			console.log('No url');
			return;
		} else if (!option) {
			const fetchUserData = async () => {
				try {
					const response = await fetch(this.url + this.id, {
						method: 'GET',
					});
					const data = await response.json();
					this.datas = data;
					console.log(data);
				} catch (error) {
					console.log(error);
				} finally {
					return this.datas;
				}
			};
			return fetchUserData;
		} else {
			const fetchUserData = async () => {
				try {
					const response = await fetch(this.url + this.id + option, {
						method: 'GET',
					});
					const data = await response.json();
					this.datas = data;
				} catch (error) {
					console.log(error);
				} finally {
					return this.datas;
				}
			};
			return fetchUserData;
		}
	}
}
