import React, { useContext } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer, Customized } from 'recharts';

import useFetch from '../../../utils/hooks/hook';
import { UserContext } from '../../../utils/context/userContext';

import { ActivityModel } from '../../../models/ActivityModel';

import Loader from '../../Loader/Loader';

import './ActivityGraph.css';

const ActivityGraph = () => {
	const [userId] = useContext(UserContext);
	const { data, loading } = useFetch(userId, 'activity');

	if (loading === true) {
		return <Loader />;
	} else {
		const activityDatas = new ActivityModel(data);
		const processedData = activityDatas.getSessions();
		// console.log(processedData);

		return (
			<div className="activityGraph">
				<h3>Activité quotidienne</h3>
				<ResponsiveContainer minWidth={'100%'} minHeight={320}>
					<BarChart
						barGap={8}
						data={processedData}
						margin={{
							top: 5,
							right: 30,
							left: 43,
							bottom: 5,
						}}
					>
						<CartesianGrid strokeDasharray="3 3" vertical={false} />
						<XAxis dataKey="name" scale="point" padding={{ left: 10, right: 10 }} />
						<YAxis
							orientation="right"
							tickLine={false}
							axisLine={false}
							tickMargin={45}
							minTickGap={27}
							tick={{ fontSize: 14 }}
						/>
						<Tooltip />
						<Legend
							iconType="circle"
							verticalAlign="top"
							align="right"
							wrapperStyle={{ top: 35, right: 25 }}
							payload={[
								{
									value: 'Poids (kg)',
									color: 'var(--primary-color)',
								},
								{
									value: 'Calories brûlées (kcal)',
									color: 'var(--secondary-color)',
								},
							]}
						/>
						<Bar
							// name="Poids (kg)"
							dataKey="weight"
							fill="#282D30"
							barSize={7}
							radius={[3, 3, 0, 0]}
							unit="kg"
						/>
						<Bar
							// name="Calories brûlées (kcal)"
							dataKey="calories"
							fill="#E60000"
							barSize={7}
							radius={[3, 3, 0, 0]}
							unit="kcal"
						/>
					</BarChart>
				</ResponsiveContainer>
			</div>
		);
	}
};

export default ActivityGraph;
