import React from 'react';
import { useState } from 'react';
import useFetch from '../hooks/hook';

export const Context = React.createContext();

const ContextProvider = ({ children }) => {
	const [user12, setUser12] = useState([]);
	// const [user18, setUser18] = useState([]);

	const { data12, isError12, loading12, errorMessage12 } = useFetch(`http://localhost:8080/user/12`);
	setUser12(data12, isError12, loading12, errorMessage12);

	// const { data18, isError18, loading18, errorMessage18 } = useFetch(`http://localhost:8080/user/18`);
	// setUser18(data18, isError18, loading18, errorMessage18);

	return <Context.Provider value={{ user12 }}>{children}</Context.Provider>;
};

export default ContextProvider;
