import React, { useContext } from 'react';
import { BarChart, Bar, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

import useFetch from '../../../utils/hooks/hook';
import { UserContext } from '../../../utils/context/userContext';

import { ActivityModel } from '../../../models/ActivityModel';

import Loader from '../../Loader/Loader';

import './ActivityGraph.css';

const ActivityGraph = () => {
	const [userId] = useContext(UserContext);
	const { data, loading } = useFetch(userId, 'activity');

	if (loading === true) {
		return <Loader />;
	} else {
		const averageSessions = new ActivityModel(data);
		const processedData = averageSessions.getSessions();
		console.log(processedData);

		return (
			<div className="activityGraph" style={{ width: '100%', height: '100%' }}>
				<ResponsiveContainer width="100%">
					<BarChart
						// width={500}
						// height={300}
						data={processedData}
						margin={{
							top: 5,
							right: 30,
							left: 20,
							bottom: 5,
						}}
					>
						<CartesianGrid strokeDasharray="3 3" />
						<XAxis dataKey="name" />
						<YAxis />
						<Tooltip />
						<Legend />
						<Bar dataKey="weight" fill="#8884d8" />
						<Bar dataKey="calories" fill="#82ca9d" />
					</BarChart>
				</ResponsiveContainer>
			</div>
		);
	}
};

export default ActivityGraph;
