import { Component } from 'react';

const props = 12;

class Profile extends Component {
	// constructor(props) {
	// 	super(props);
	// 	this.state = {
	// 		user: {},
	// 		loading: true,
	// 		isError: false,
	// 		errorMessage: '',
	// 	};
	// }

	componentDidMount() {
		// const { id } = this.props.match.params;
		const { id } = 12;
		const url = 'http://localhost:8080/users/';
		fetch(url + id, {
			method: 'GET',
			headers: {
				'Content-Type': 'application/json',
				Accept: 'application/json',
			},
		})
			.then((response) => response.json())
			.then((data) => {
				this.setState({ user: data.data, loading: false });
			})
			.catch((error) => {
				this.setState({
					isError: true,
					errorMessage: error.message,
					loading: false,
				});
			});
	}

	render() {
		const { user, loading, isError, errorMessage } = this.state;
		if (loading) {
			return <div>Loading...</div>;
		}
		if (isError) {
			return <div>{errorMessage}</div>;
		}
		return (
			<div className="profile">
				<img src={user.avatar} className="profile__avatar" alt="Avatar by Gustavo Pedrosa" />
				<h3 className="profile__name">{user.name}</h3>
				<p className="profile__email">{user.email}</p>
			</div>
		);
	}
}

export default Profile;
