export class PerformanceModel {
	constructor(data) {
		this.id = data.data.userId;
		this.kind = data.kind;
		this.kindValues = data.data;
	}

	getProcessedDatas() {
		let kindValues = this.kindValues;
		let datas = [];
		let values = [];
		kindValues.forEach((value) => {
			values.push(value.value);
		});

		kindValues.forEach((kindValue) => {
			datas.push({
				subject: Object.values(this.kind)[kindValue.kind - 1],
				value: kindValue.value,
				fullMark: Math.max(...values),
			});
		});
		return datas;
	}
}
